import React, { useState } from 'react';
import './App.css';

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [result, setResult] = useState('');
  const [theme, setTheme] = useState('light');
  const [previewUrl, setPreviewUrl] = useState(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreviewUrl(reader.result);
      };
      reader.readAsDataURL(file);
    } else {
      setPreviewUrl(null);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!selectedFile) {
      alert('ËØ∑ÂÖàÈÄâÊã©‰∏Ä‰∏™Êñá‰ª∂');
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      setResult('Ê≠£Âú®ËØÜÂà´...');
      const response = await fetch('http://localhost:5000/predict', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      setResult(`ËøôÊòØ‰∏ÄÂè™${data.class_name}ÔºÅÁΩÆ‰ø°Â∫¶Ôºö${(data.confidence * 100).toFixed(2)}%`);
    } catch (error) {
      console.error('Error:', error);
      setResult('ËØÜÂà´Â§±Ë¥•ÔºåËØ∑ÈáçËØï');
    }
  };

  const toggleTheme = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  return (
    <div className={`App ${theme}`}>
      <button className="theme-toggle" onClick={toggleTheme}>
        {theme === 'light' ? 'üåô' : '‚òÄÔ∏è'}
      </button>
      <header className="App-header">
        <h1>Áå´ÁãóÂàÜÁ±ªÂô®</h1>
      </header>
      <form onSubmit={handleSubmit} className="upload-form">
        <label className="custom-file-upload">
          <input type="file" onChange={handleFileChange} accept="image/*" />
          ÈÄâÊã©ÂõæÁâá
        </label>
        <br />
        {previewUrl && <img src={previewUrl} alt="Preview" className="image-preview" />}
        <br />
        <button type="submit">ËØÜÂà´ÂõæÁâá</button>
      </form>
      {result && <div className="result">{result}</div>}
    </div>
  );
}

export default App;
